import React from "react";

const axios = require('axios').default;

export default function Search(){

    const [inputState, setInputState] = React.useState([])
    const [responseState, setResponseState] = React.useState([])

    function inputBoxChange(event){
        event.preventDefault()
        setInputState(event.target.value)

    }

    function inputBoxSubmit(event){   
        event.preventDefault()

            axios.get(`https://api.thedogapi.com/v1/breeds/search?q=${inputState}`)
            .then(responce =>{ 
            console.log(responce.data)
            })
            .catch(error => console.log(error))       

    }

    return(
        <div>
           <h1>Use the input box to search for dogs. If you like a dog it will be added to your list.</h1>

           <form onSubmit={inputBoxSubmit}>
            <input onChange={inputBoxChange}/>
            <button>Submit</button>
           </form>
        </div>
    )
}
