import React from "react";
import { AppContext } from "./Context";

const axios = require('axios').default;

export default function Search(){

const {getRequest, randomAnimal, like, buttonStatus} = React.useContext(AppContext)  

function test1 (){
    if (randomAnimal.length  === 0){
        return(0)
    }
    else{
        return(1)
    }
}
console.log(test1())
console.log(randomAnimal)

    return(
        <div className="searchDiv">
           <h5>Use the input box to search for a random image of a zoo animal. If you want to save it to your list, hit Like!</h5>

                <button onClick={getRequest}>New Image</button>

            <div>
                <span>Name: {randomAnimal.name}</span>
                    <br></br>
                <img src={`${randomAnimal.image_link}`}/>
                    <br></br>
                <span>Geological Range: {randomAnimal.geo_range}</span>
                    <br></br>
                <span>Habitat: {randomAnimal.habitat}</span>
                    <br></br>
                <span>Diet: {randomAnimal.diet}</span>
            </div>

                <button disabled = {buttonStatus} onClick={like}>Like</button>




        </div>
    )
}
