import React, {useState, useEffect} from "react";

const AppContext = React.createContext()
const axios = require('axios').default;

function ContextProvider(props){

    const [randomAnimal, setRandomAnimal] = React.useState([])


    function getRequest(){
        axios.get("https://zoo-animal-api.herokuapp.com/animals/rand")
        .then(responce =>{ 
            console.log(responce.data)
        setRandomAnimal(responce.data)
        })
        .catch(error => console.log(error))

    }


    return(
        <AppContext.Provider value={{getRequest : getRequest, randomAnimal : randomAnimal}}>
            {props.children}
        </AppContext.Provider>
    )
}

export {AppContext, ContextProvider}
