const axios = require('axios').default;

export default function Search(){

    const [randomState, setRandomState] = React.useState([])


        function getRequest(){
            axios.get("https://zoo-animal-api.herokuapp.com/animals/rand")
            .then(responce =>{ 
                console.log(responce.data)
            setRandomState(responce.data)
            })
            .catch(error => console.log(error))

        }




    return(
        <div>
           <h1>Use the input box to search for a random image of a zoo animal. If you like it hit like!</h1>

          <button onClick={getRequest}>New Image</button>

          <div>
            <h1>{randomState.name}</h1>
            <img className="randomImage" src= {`${randomState.image_link}`}/>
                <span>{randomState.diet}</span>
                <span></span>
                <span></span>




          </div>



        </div>
    )
}
