import React, {useState, useEffect} from "react";

const AppContext = React.createContext()
const axios = require('axios').default;

function ContextProvider(props){

    const [randomAnimal, setRandomAnimal] = React.useState([])
    const [animalList, setAnimalList] = React.useState([])
    const {buttonStatus, setButtonStatus} = React.useState(false)


    function getRequest(){
        axios.get("https://zoo-animal-api.herokuapp.com/animals/rand")
        .then(responce =>{ 
        setRandomAnimal(responce.data)
        })
        .catch(error => console.log(error))
    }


    function like(event){
        event.preventDefault()
        setButtonStatus(true)

        setAnimalList(prevState =>{
            return[
                ...prevState,
             randomAnimal
            ]
        })
        console.log(animalList)

    }


    return(
        <AppContext.Provider value={{getRequest : getRequest, randomAnimal : randomAnimal, like : like, animalList : animalList}}>
            {props.children}
        </AppContext.Provider>
    )
}

export {AppContext, ContextProvider}
