import React from "react";

export default function App(){

    const [typedContent, setTypedContent] = React.useState([])
    const [readOnly, setReadOnly] = React.useState(true)


    function running(){
        setReadOnly(false)

        setTimeout(() => { 
            setReadOnly(true)           
        }, 3000)
    }

    function typedFunction(event){
        event.preventDefault()
       setTypedContent(event.target.value)
    }

console.log(readOnly)


    return(
        <div className="mainDiv">
            <h1>How Fast Can You Type?</h1>

            <textarea readOnly = {readOnly} className="textArea" name="typedContent" onChange={typedFunction}/>
            <h1>Time Remaining</h1>
            <button onClick={running}>START</button>
            <h4>You typed {typedContent.length}  letters</h4>


        </div>
    )
}
