import React from "react";

export default function App(){

    const [typedContent, setTypedContent] = React.useState("")
    // const [readOnly, setReadOnly] = React.useState(true)
    const [timeRemaining, setTimeRemaining] = React.useState(3)


    function running(){
      setTimeout(() => {
          setTimeRemaining(timeRemaining - 1)
      }, 3000);
    }

    function typedFunction(event){
        event.preventDefault()
       setTypedContent(event.target.value)
    }


    function wordCount(){
        if(typedContent === ""){
            return ("0")
        }
        else{
        return( typedContent.trim("").split(" ").length)
        }
    }




    return(
        <div className="mainDiv">
            <h1>How Fast Can You Type?</h1>

            <textarea className="textArea" name="typedContent" onChange={typedFunction}/>
            <h1>Time Remaining: {timeRemaining}</h1>
            <button onClick={running}>START</button>
            <h4>You typed  words</h4>


        </div>
    )
}
