import React, { useContext, useState } from "react";
import { AppContext } from "./Context";


export default function Card(props){

    const {deleteFunction, editFunction, saveEdit} = useContext(AppContext)

    const [editBoolean, setEditBoolean] = useState(false)
    const [editedContent, setEditedContent] = useState([])


    function toggleEdit(event){
       event.preventDefault()
       setEditBoolean(!editBoolean)
    }

    function handleSubmit(event){
        event.preventDefault()
        setEditBoolean(!editBoolean)
        saveEdit()
    }


    return(
        <div>
            <form onSubmit={handleSubmit}>
                <h1>{props.title} </h1>
                <input name="title" style={{display: editBoolean ? "block" : "none"}}/>
                <img src= {props.url}/>
                <input style={{display: editBoolean ? "block" : "none"}}/>
                <h4>{props.description}</h4>
                <input style={{display: editBoolean ? "block" : "none"}}/> 
                <button style={{display: editBoolean ? "block" : "none"}}>Submit Edits</button>               
            </form>


           <button value={props.value} onClick={deleteFunction}>Delete</button>
           <button style={{display: editBoolean ? "none" : "block"}} value={props.value} onClick={toggleEdit}>Edit</button>
        </div>
    )
}
