import React, {useState} from "react";
import Card from "./Card";


const AppContext = React.createContext()
const axios = require('axios').default;


function ContextProvider(props){

    const [inputState, setInputState] = useState([])
    const [listState, setListState] = useState([])


    function inputBoxOnChange(event){
        event.preventDefault()
        setInputState(prevState => {
            return{
                ...prevState,
                [event.target.name] : event.target.value
            }
        })
    }


    function inputBoxSubmit(event){
        event.preventDefault()
        document.getElementById("inputForm").reset()


        setInputState(prevState =>{
            return{
                prevState,
                _id : 1
            }
        })


        setListState(prevState => {
            return[
                ...prevState,
                inputState
            ]
        })        

        axios.post("https://api.vschool.io/<NicholasBielecki>/thing/69", inputState)
    .then(response => console.log(response.data))
    .catch(error => console.log(error))



    setInputState([])
    }


    function deleteFunction(event){
        event.preventDefault()  
        setListState(prevState => {
            prevState.splice(event.target.value, 1)
            return[...prevState]
        })
    }

    function saveEdit(event, editedContent){
        setListState(prevState =>{
            prevState.splice(event.target.value, 1)
            // prevState[event.target.value] = editedContent
            return[...prevState,
                editedContent
            ]
        })
    }

    function getRequest(){
        axios.get("https://api.vschool.io/<NicholasBielecki>/thing/")
        .then(responce =>{
            let newData = responce
           return(newData.data.length)
        }
            )
    }


    let mappedList = listState.map( (param1, index) =>{
        return(
            <Card 
            key={index}
            title = {param1.title}
            url = {param1.url}
            description = {param1.description}
            value = {index}
            />
        )
    })


    return(
<AppContext.Provider  value={{inputBoxSubmit : inputBoxSubmit, inputBoxOnChange : inputBoxOnChange, deleteFunction : deleteFunction, saveEdit : saveEdit }}>
   {props.children}
   {mappedList}
</AppContext.Provider>
    )
}


export {AppContext, ContextProvider}
