import React, { useContext, useState } from "react";
import { AppContext } from "./Context";


export default function Card(props){

    const {deleteFunction, saveEdit} = useContext(AppContext)
    //The boolean is used to either hide or show the edit form. 
    const [editBoolean, setEditBoolean] = useState(false)
    const [editedContent, setEditedContent] = useState([])


    function toggleEdit(event){
       event.preventDefault()
       setEditBoolean(!editBoolean)
    }

    function handleChange(event){
        event.preventDefault()
        setEditedContent(prevState => { 
            return{
            ...prevState,
            [event.target.name] : event.target.value
        }

        })}

    function handleSubmit(event){
        event.preventDefault()
        setEditBoolean(!editBoolean)
        saveEdit(event, editedContent)
    }


    return(
        <div>
            <form value={props.value} onSubmit={handleSubmit}>
                <h1>{props.title} </h1>
                <input onChange={handleChange} name="title" style={{display: editBoolean ? "block" : "none"}}/>
                <img src= {props.url} alt=""/>
                <input onChange={handleChange} name="imgUrl" style={{display: editBoolean ? "block" : "none"}}/>
                <h4>{props.description}</h4>
                <input onChange={handleChange} name="description" style={{display: editBoolean ? "block" : "none"}}/> 
                <button value={props.value} style={{display: editBoolean ? "block" : "none"}}>Submit Edits</button>               
            </form>


           <button value={props.value} onClick={deleteFunction}>Delete</button>
           <button value={props.value} style={{display: editBoolean ? "none" : "block"}}  onClick={toggleEdit}>Edit</button>
        </div>
    )
}
