import React, { useContext, useState } from "react";
import { AppContext } from "./Context";


export default function Card(props){

    const {deleteFunction, editFunction} = useContext(AppContext)
    const [editBoolean, setEditBoolean] = useState(false)

    function toggleEdit(event){
       editFunction(event);
       setEditBoolean(!editBoolean)
       console.log(editBoolean)
    }


    return(
        <div>
           <h1>{props.title} </h1>
           <input style={{display: editBoolean ? "block" : "none"}}/>
           <img src= {props.url}/>
           <input style={{display: editBoolean ? "block" : "none"}}/>
           <h4>{props.description}</h4>
           <input style={{display: editBoolean ? "block" : "none"}}/>

           <button value={props.value} onClick={deleteFunction}>Delete</button>
           <button value={props.value} onClick={toggleEdit}>{editBoolean ? "Save" : "Edit"}</button>
        </div>
    )
}
