        console.log(uglyThingsState)
        setUglyThingsState(prevState => {

            return{
                ...prevState,
                thisTest : "this is a test"
            }
        })
    }

    return(
<AppContext.Provider value={{inputBoxSubmit : inputBoxSubmit}}>
   {props.children}
</AppContext.Provider>
    )
}


