import React, { useContext, useState } from "react";
import { AppContext } from "./Context";


export default function Card(props){

    const {deleteFunction, saveEdit, listState} = useContext(AppContext)
    //The boolean is used to either hide or show the edit form. 
    const [editBoolean, setEditBoolean] = useState(false)
    const [editedContent, setEditedContent] = useState([])


    //Form was not submitting value attribute however, delete button was. I created this variable as a bandaid. Ask teacher about a better way to do this using the form.
    let testVar = props.value


    function toggleEdit(event){
       event.preventDefault()
       setEditBoolean(!editBoolean)
    }

    function handleChange(event){
        event.preventDefault()
        setEditedContent(prevState => { 
            return{
            ...prevState,
            [event.target.name] : event.target.value
        }

        })}

    function handleSubmit(event){

        event.preventDefault()
        setEditBoolean(!editBoolean)
        saveEdit(testVar, editedContent)
    }

    //Template used to create a Div from API. I give each card a value so I edit or delete it at the proper index. 
    let mappedList = listState.map( (param1, index) =>{
        return(
            // <Card 
            // key={index}
            // title = {param1.title}
            // url = {param1.url}
            // description = {param1.description}
            // value = {index}
            // />

            <div className="card" value={props.value}>
            <form value= "test" onSubmit={handleSubmit}>
                <h1>{props.title} </h1>
                <input onChange={handleChange} name="title" style={{display: editBoolean ? "block" : "none"}}/>
                <img src= {props.url} alt=""/>
                <input onChange={handleChange} name="imgUrl" style={{display: editBoolean ? "block" : "none"}}/>
                <h4>{props.description}</h4>
                <input onChange={handleChange} name="description" style={{display: editBoolean ? "block" : "none"}}/> 
                <button value= "test" style={{display: editBoolean ? "block" : "none"}}>Submit Edits</button>               
            </form>


           <button className="cardButton" value={props.value} onClick={deleteFunction}>Delete</button>
           <button className="cardButton" value= "test" style={{display: editBoolean ? "none" : "block"}}  onClick={toggleEdit}>Edit</button>
        </div>
        )
    })


    return(
        <div>
            {mappedList}
        </div>
    )
}
