    let editBoolean = false

    function toggleEditBoolean(){
        // editBoolean = !editBoolean
        console.log("worked")
    }


    return(
        <div>
           <h1>{props.title} <input style={{display: editBoolean ? "block" : "none"}}/></h1>
           <img src= {props.url}/>
           <h4>{props.description}</h4>
           <button value={props.value} onClick={deleteFunction}>Delete</button>
           <button value={props.value} onClick={()=> {deleteFunction(); toggleEditBoolean()}}>Edit</button>
        </div>
    )
}
